export const AppScreen = () => {
    const { isLoading, setIsLoading, auth } = useContext(AppContext);
    const [ extendedBar, setExtendedBar ] = useState(true);
    const [ lightTheme, setLightTheme ] = useState(false);

    useEffect(() => {
        console.log("iniciado componente app");
        setIsLoading(true);

        console.log("simulacion de carga de datos");
        console.log(auth);

        setTimeout(() => {
            console.log("deteniendo carga de datos...");
            setIsLoading(false);
            console.log(isLoading);
        }, 2500);
    }, [setIsLoading, auth]);

    if (isLoading) {
        return(<LoadingScreen />)
    } else {
        return(
            <div className={`app__wrapper ${extendedBar ? 'side-bar__extended' : ''} ${lightTheme ? 'light-mode': 'dark-mode'}`}>
                <SideBar sideBar_status={extendedBar} sideBar_set={setExtendedBar} theme={lightTheme} setTheme={setLightTheme}/>
                <Routes>
                    <Route path="/feed" element={<FeedPanel />} />
                    <Route path="/explore" element={<ExplorePanel />} />
                    <Route path="/channels" element={<ChannelsPanel />} />
                    <Route path="/collections" element={<CollectionsPanel />} />
                    <Route path="/notifications" element={<NotificationsPanel />} />
                    <Route path="/config" element={<ConfigPanel />} />
