/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'
import { AppContext } from '../../scripts/app_context';
import { Routes, Route, Link, useLocation } from 'react-router-dom';

/*----- Recursos multimedia -----*/
import default_bg from '../../imgs/loading_background.jpg';
import svg_icons from '../../icons/svg-resources.xml';

/*----- Funciones Generales -----*/
function formatedDate(dateString){
    const raw_date = new Date(dateString);
    const months = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

    const formated_date = `${raw_date.getDate().toString().padStart(2, '0')} de ${months[raw_date.getMonth()]} del ${raw_date.getFullYear()} ● ${raw_date.getHours()}:${raw_date.getMinutes().toString().padStart(2, '0')}`;

    return formated_date
}

/*----- Componentes Auxiliares -----*/
const TagsHandler = ({input_tags}) => {
    let local_elements = [];
    input_tags.forEach((data, index) => {
        const localExisting_element = local_elements.find(element => element.key === index);
        if (!localExisting_element) {
            if (local_elements.length < 3 ) {
                local_elements.push(
                    <div key={index} className='tagLabel'><p>#{data}</p></div>
                )
            } else if (local_elements.length < 4 && input_tags.length >= 4) {
                local_elements.push(
                    <div key={4} className='tagLabel'><p>{`+${input_tags.length - 3}`}</p></div>
                )
            }
        }
    });
    return local_elements;
}

const PointsHandler = ({input_points}) => {
    return (
        <>
            <button className='button iconed'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
            <p>{input_points.total}</p>
            <button className='button iconed down'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
        </>
    )
}

/*----- Componentes locales -----*/
const PublicationViewer = ({getter_post_id}) => {
    const [ visiblePostViewer, setVisiblePostViewer ] = useState(false);
    const [ publicationInfo, setPublicationInfo ] = useState(null);

    useEffect(() => {
        if (getter_post_id) {
            setTimeout(() => { /* simulacion de busqueda de datos */
                setVisiblePostViewer(true)
                setPublicationInfo({
                    content: {
                        author_pic: null,
                        author: "tester 3000",
                        title: "Titulo de testeo Titulo de testeo Titulo de testeo Titulo de testeo Titulo de testeo Titulo de teste",
                        date: formatedDate("2024-06-19T06:30:00"),
                        text: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante",
                        tags: ["testing", "development", "post", "bot", "ia"],
                        points: {
                            total: 5, 
                            current_user: null
                        }
                    },
                    comments: {
                        total: 1,
                        comments_data: [
                            {author: "supra_tester", profilePic_url: "https://wallpapercave.com/wp/wp8142396.jpg", date: formatedDate("2024-06-20T10:58:00"), content: "nice done!", media: null}
                        ]
                    }
                })
            }, 1000);
        }
    }, [getter_post_id]);

    function close_handler () {
        setVisiblePostViewer(false)
        setTimeout(() => {
            setPublicationInfo(null);
        }, 1000);
    }

    const PublicationCommentsHandler = ({input_commentsData}) => {
        const local_elements = [];

        if (input_commentsData.comments_data) {
            input_commentsData.comments_data.forEach((data, index) => {
                const localExisting_element = local_elements.find(element => element.key === index);
                if (!localExisting_element) {
                    local_elements.push(
                        <div key={index} className='message__wrapper forum-style'>
                            <div className='user-avatar msg-style'>
                                {data.profilePic_url ?
                                <img src={data.profilePic_url} alt="user_pic" /> :
                                <svg className='icon'>
                                    <use xlinkHref={svg_icons+'#icon-empty-user'}></use>
                                </svg>}
                            </div>
                            <div className='message__metadata'>
                                <h3>{data.author}</h3>
                                <p>{data.date}</p>
                            </div>
                            <div className='message__content'>
                                {data.content ? <section>{data.content}</section> : ''}
                                {data.media ? <section>{data.media}</section> : ''}
                            </div>
                        </div>
                    )
                }
            });
        } else {
            local_elements.push(
                <div className='null-contents__wrapper'>
                    nada por aqui OwO
                </div>
            )
        }

        return local_elements
    }

    if (publicationInfo) {
        return (
            <div className={`publications-aside__wrapper ${visiblePostViewer ? '' : 'hidden'}`}>
                <div className='publications-aside__content'>
                    <div className='publication__header'>
                        <div className='publication_title'>
                            <h3>{publicationInfo.content.title}</h3>
                            <hr className='separator full-vertical' />
                            <p>{publicationInfo.content.date}</p>
                        </div>
                        <div className='publication_options'>
                            <button className='button iconed' onClick={close_handler}>
                                <svg className='icon'>
                                    <use xlinkHref={svg_icons+'#icon-cross'}></use>
                                </svg>
                            </button>
                        </div>
                    </div>
                    <div className='publication__contents'>
                        <div className='content__header'>
                            <div className='content__author'>
                                <div className='user-avatar publication-style'>
                                    {publicationInfo.content.author_pic ?
                                        <img src={publicationInfo.content.author_pic} alt="user_pic" /> :
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-empty-user'}></use>
                                        </svg>
                                    }
                                </div>
                                <p className='author-label'>{publicationInfo.content.author}</p>
                            </div>
                            <div className='content__title'>
                                <h2>{publicationInfo.content.title}</h2>
                            </div>
                            <div className='content__tags'>
                                <TagsHandler input_tags={publicationInfo.content.tags}/>
                            </div>
                        </div>
                        <hr className='separator full-horizontal'/>
                        <div className='content__text'>
                            {publicationInfo.content.text}
                        </div>
                        <div className='content__media'></div>
                        <div className='content__footer'>
                            <div className='points-section'>
                                <PointsHandler input_points={publicationInfo.content.points}/>
                            </div>
                        </div>
                    </div>
                    <div className='publication__comments'>
                        <PublicationCommentsHandler input_commentsData={publicationInfo.comments} />
                    </div>
                </div>
            </div>
        )
    } else {
        return (
            <div className={`publications-aside__wrapper ${visiblePostViewer ? '' : 'hidden'}`}>
                <div className='publications-aside__content'>
                    <button onClick={close_handler}>Cerrar</button>
                    <h3>cargando...</h3>
                </div>
            </div>
        )
    }
}

const Publications_Section = ({ input_postList }) => {
    const [ galleryElements, setGalleryElements ] = useState([]);
    const [ listElements, setListElements ] = useState([]);
    const [ galleryMode, setGalleryMode ] = useState(true);
    const { commonHost } = useContext(AppContext);
    const [ localLoading, setLocalLoading ] = useState(true);
    const [ currentPost, setCurrentPost] = useState('0');

    useEffect(() => {
        if (!localLoading) {setLocalLoading(true)}
        if (input_postList) {
            input_postList.forEach((data, index) => {
                const galleryExistingElement = galleryElements.find(element => element.key === `card-gallery-${data.id}`);
                const listExistingElement = listElements.find(element => element.key === `card-list-${data.id}`);
                if (!galleryExistingElement) {
                    setGalleryElements(prevLista => prevLista.concat(<GalleryView key={`card-gallery-${data.id}`} input_post={data} input_host={commonHost}/>));
                }
                if (!listExistingElement) {
                    setListElements(prevLista => prevLista.concat(<ListView key={`card-list-${data.id}`} input_post={data} input_host={commonHost}/>))
                }
            });
            setLocalLoading(false)
        } else {
            setGalleryElements(prevLista => prevLista.concat(<h2>sin datos...</h2>));
            setListElements(prevLista => prevLista.concat(<h2>sin datos...</h2>));
            setLocalLoading(false)
        }
    }, [localLoading, setLocalLoading, galleryElements, input_postList, commonHost]);

    const ListView = ({ input_post, input_host }) => {
        return (
            <div data-id={input_post.id} className='card__wrapper'>
                <div className='card__content'>
                    <div className='card__header'>
                        <div className='post_tags'>
                            <TagsHandler input_tags={input_post.tags}/>
                        </div>
                        <h3 className='post_title'>{input_post.tittle}</h3>
                    </div>
                    <div className='card__postBody'>
                        <div className='post_content'>
                            <div className='post_author'>
                                <p>{input_post.author}:</p>
                            </div>
                            <div className='post_content-text'>
                                <p>{input_post.content}</p>
                            </div>
                        </div>
                    </div>
                    <div className='card__footer'>
                        <div className='post_points'>
                            <PointsHandler input_points={input_post.points}/>
                        </div>
                        <div className='post_comments-number'>
                            <svg className='icon'>
                                <use xlinkHref={svg_icons+'#icon-comment'}></use>
                            </svg>
                            <p>{input_post.n_comments}</p>
                        </div>
                        <div className='post_date'><p>{formatedDate(input_post.createdDate)}</p></div>
                    </div>
                </div>
                {input_post.media ?
                    <div className='card__media'>
                        <img src={input_post.media[0]} alt={`post-first-image`} />
                    </div> : ''
                }
            </div>
        )
    }

    const GalleryView = ({ input_post, input_host }) => {
        const PicturesHandler = ({input_images}) => {
            let local_elements = [];

            input_images.forEach((data, index) => {
                const localExisting_element = local_elements.find(element => element.key === index);
                if (!localExisting_element) {
                    console.log(`caso de indice ${index}; lista de entrada = ${input_images.length}`)
                    if (local_elements.length < 4) {
                        if (input_images.length === 1) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column full-row'>
                                    <img src={data} alt={`post-image-${1}`} />
                                </div>
                            )
                        } else if (input_images.length === 2) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            )
                        } else if (input_images.length === 3) {
                            if (index === 0) {
                                local_elements.push(
                                    <div key={index} className='imageSection full-column'>
                                        <img src={data} alt={`post-image-${index}`} />
                                    </div>
                                )  
                            } else {
                                local_elements.push(
                                    <div key={index} className='imageSection'>
                                        <img src={data} alt={`post-image-${index}`} />
                                    </div>
                                )    
                            }
                        } else {
                            local_elements.push(
                                <div key={index} className='imageSection'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            ) 
                        }
                    }
                }
            });
            return local_elements;
        }

        return (
            <div data-id={input_post.id} className='card__wrapper'>
                <div className='card__header'>
                    <section>
                        <div className='post_author'><p>{input_post.author}</p></div>
                        <p className='post_date'>{formatedDate(input_post.createdDate)}</p>
                    </section>
                    <h3 className='post_title'>{input_post.tittle}</h3>
                </div>
                <div className='card__postBody'>
                    {input_post.media ?
                        <div className='body-media'><PicturesHandler input_images={input_post.media}/></div>:
                        <div className='post_content-text'><p>{input_post.content}</p></div>
                    }
                    <div className='post_tags'>
                        <TagsHandler input_tags={input_post.tags}/>
                    </div>
                </div>
                <div className='card__footer'>
                    <div className='post_comments-number'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-comment'}></use>
                        </svg>
                        <p>{input_post.n_comments}</p>
                    </div>
                    <div className='post_points'>
                        <PointsHandler input_points={input_post.points}/>
                    </div>
                </div>
            </div>
        );
    }

    const PostHandler = () => {
        if (localLoading) {
            return <h2>Cargando...</h2>
        } else {
            if (galleryMode) {return galleryElements} else {return listElements}
        }
    }

    return(
        <div className='publications__wrapper'>
            <div className='publications__content'>
                <div className='header'>
                    <div className='post-searcher-creator'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-search'}></use>
                        </svg>
                        <input className='title_mod' type='text' placeholder='titulo a buscar o crear' />
                        <button className='button interaction-option'>
                            <svg className='icon'>
                                <use xlinkHref={svg_icons+'#icon-edit'}></use>
                            </svg>
                            <p>Nueva publicacion</p>
                        </button>
                    </div>
                    <div className='posts-filter'>
                        <div className='button-dropdown'>
                            <div className='button-dropdown__toggler'>
                                {galleryMode ?
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                        </svg>
                                        <span>Vista de galeria</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div> :
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                        </svg>
                                        <span>Vista de Lista</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div>
                                }
                            </div>
                            <div className='button-dropdown__content'>
                                <button className='option' onClick={() => setGalleryMode(true)}>
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                    </svg>
                                    <span>Vista de galeria</span>
                                </button>
                                <button className='option' onClick={() => setGalleryMode(false)}>
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                    </svg>
                                    <span>Vista de Lista</span>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                <div className={`publications ${galleryMode ? 'gallery' : 'list'}`}>
                    <PostHandler />
                </div>
            </div>
            <PublicationViewer getter_post_id={currentPost} />
        </div>
    );
}

const Information_Section = () => {
    return(
        <div className='publications__wrapper'>INFORMATION</div>
    );
}

const Gallery_Section = () => {
    return(
        <div className='publications__wrapper'>GALLERY</div>
    );
}

const Collections_Section = () => {
    return(
        <div className='publications__wrapper'>COLLECTIONS</div>
    ); 
}

/*----- Componente central -----*/
export const ProfilePanel = () => {
    const { username } = useParams();
    const { auth, userData } = useContext(AppContext);
    const [ tabberLoc, setTabberLoc ] = useState('one');
    const current_url = useLocation();
    const postList_Test = [
        {id: 1, points: {total: 15}, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["bot"], tittle: "testing", media: null, content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 2, points: {total: 10}, createdDate: "2024-06-19T10:00:00", author: "tester 2", n_comments: 7, tags: ["development", "post"], tittle: "testing 2", media: [default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 3, points: {total: 0}, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["programando", "bot", "ia"], tittle: "testing", media: [default_bg, default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 4, points: {total: 1}, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["testing", "development", "post", "bot", "ia"], tittle: "testing", media: [default_bg, default_bg, default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
