/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'
import { AppContext } from '../../scripts/app_context';
import { Routes, Route, Link, useLocation } from 'react-router-dom';

/*----- Recursos multimedia -----*/
import default_bg from '../../imgs/loading_background.jpg';
import svg_icons from '../../icons/svg-resources.xml';

/*----- Componentes locales -----*/
const Publications_Section = ({ input_postList }) => {
    const [ galleryElements, setGalleryElements ] = useState([]);
    const [ listElements, setListElements ] = useState([]);
    const [ galleryMode, setGalleryMode ] = useState(true);
    const { commonHost } = useContext(AppContext);
    const [ localLoading, setLocalLoading ] = useState(true);

    useEffect(() => {
        if (!localLoading) {setLocalLoading(true)}
        if (input_postList) {
            input_postList.forEach((data, index) => {
                const galleryExistingElement = galleryElements.find(element => element.key === `card-${data.id}`);
                const listExistingElement = listElements.find(element => element.key === `card-${data.id}`);
                if (!galleryExistingElement) {
                    setGalleryElements(prevLista => prevLista.concat(<GalleryView key={`card-${data.id}`} input_post={data} input_host={commonHost}/>));
                }
                /*if (!listExistingElement) {
                    setListElements(prevLista => prevLista.concat(<ListView key={`card-${index}`} input_post={data} input_host={commonHost}/>))
                }*/
            });
            setLocalLoading(false)
        } else {
            setGalleryElements(prevLista => prevLista.concat(<h2>sin datos...</h2>));
            /*setListElements(prevLista => prevLista.concat(<h2>sin datos...</h2>))*/
            setLocalLoading(false)
        }
    }, [localLoading, setLocalLoading, galleryElements, input_postList, commonHost]);

    const ListView = ({ input_post, input_host }) => {

    }

    const GalleryView = ({ input_post, input_host }) => {
        const PicturesHandler = ({input_images}) => {
            let local_elements = [];

            input_images.forEach((data, index) => {
                const localExisting_element = local_elements.find(element => element.key === index);
                if (!localExisting_element) {
                    console.log(`caso de indice ${index}; lista de entrada = ${input_images.length}`)
                    if (local_elements.length < 3) {
                        if (input_images.length === 1) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column full-row'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            )
                        } else if (input_images.length === 1) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column half-row'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            )
                        }
                    }
                }
            });
            return local_elements;
        }
        const TagsHandler = ({input_tags}) => {
            let local_elements = [];
            input_tags.forEach((data, index) => {
                const localExisting_element = local_elements.find(element => element.key === index);
                if (!localExisting_element) {
                    if (local_elements.length < 3 ) {
                        local_elements.push(
                            <div key={index} className='tagLabel'><p>{data}</p></div>
                        )
                    } else if (local_elements.length < 4 && input_tags.length >= 4) {
                        local_elements.push(
                            <div key={4} className='tagLabel'><p>{`+${input_tags.length - 3}`}</p></div>
                        )
                    }
                }
            });
            return local_elements;
        }
        return (
            <div className='card__wrapper'>
                <div className='card__header'>
                    <div><span>{input_post.author}</span><p>{input_post.createdDate}</p></div>
                    <h3>{input_post.tittle}</h3>
                </div>
                <div className='card__postBody'>
                    {input_post.media ?
                        <div className='body-media'><PicturesHandler input_images={input_post.media}/></div>:
                        <div className='body-text'><p>{input_post.content}</p></div>
                    }
                    <div className='body-tags'>
                        <TagsHandler input_tags={input_post.tags}/>
                    </div>
                </div>
                <div className='card__footer'>
                    <div>{input_post.n_comments}</div>
                    <div>points</div>
                </div>
            </div>
        );
    }

    const PostHandler = () => {
        if (localLoading) {
            return <h2>Cargando...</h2>
        } else {
            if (galleryMode) {return galleryElements} else {return listElements}
        }
    }

    return(
        <div className='publications__wrapper'>
            <div className='publications__content'>
                <div className='header'>
                    <div className='post-searcher-creator'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-search'}></use>
                        </svg>
                        <input className='title_mod' type='text' placeholder='titulo a buscar o crear' />
                        <button className='button interaction-option'>
                            <svg className='icon'>
                                <use xlinkHref={svg_icons+'#icon-edit'}></use>
                            </svg>
                            <p>Nueva publicacion</p>
                        </button>
                    </div>
                    <div className='posts-filter'>
                        <div className='button-dropdown'>
                            <div className='button-dropdown__toggler'>
                                {galleryMode ?
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                        </svg>
                                        <span>Vista de galeria</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div> :
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                        </svg>
                                        <span>Vista de Lista</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div>
                                }
                            </div>
                            <div className='button-dropdown__content'>
                                <button className='option' value="galleryView">
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                    </svg>
                                    <span>Vista de galeria</span>
                                </button>
                                <button className='option' value="listView">
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                    </svg>
                                    <span>Vista de Lista</span>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                <div className={`publications ${galleryMode ? 'gallery' : 'list'}`}>
                    <PostHandler />
                </div>
            </div>
        </div>
    );
}

const Information_Section = () => {
    return(
        <div className='publications__wrapper'>INFORMATION</div>
    );
}

const Gallery_Section = () => {
    return(
        <div className='publications__wrapper'>GALLERY</div>
    );
}

const Collections_Section = () => {
    return(
        <div className='publications__wrapper'>COLLECTIONS</div>
    ); 
}

/*----- Componente central -----*/
export const ProfilePanel = () => {
    const { username } = useParams();
    const { auth, userData } = useContext(AppContext);
    const [ tabberLoc, setTabberLoc ] = useState('one');
    const current_url = useLocation();
    const postList_Test = [
        {id: 1, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["bot"], tittle: "testing", media: null, content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 2, createdDate: "2024-06-19T10:00:00", author: "tester 2", n_comments: 7, tags: ["development", "post"], tittle: "testing 2", media: [default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 3, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["programando", "bot", "ia"], tittle: "testing", media: [default_bg, default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 4, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["testing", "development", "post", "bot", "ia"], tittle: "testing", media: [default_bg, default_bg, default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
        {id: 5, createdDate: "2024-06-19T06:30:00", author: "tester 1", n_comments: 5, tags: ["testing", "programando", "bot", "ia"], tittle: "testing", media: [default_bg, default_bg, default_bg, default_bg], content: "loremmmmmm ipsummmmmm, dolorrrrr, cosoooooo, supercosooooo, supracosoooo, no se que mas escribir, pero esto deberia resumir el texto hasta un punto especifico, pero no se cual aun, deberia verse bien, limpio y elegante"},
    ]

    let base_url = `/app/profile/${username}`

    useEffect(() => {
        if (current_url.pathname === base_url || current_url.pathname === `${base_url}/`) {setTabberLoc('one')} 
        else if (current_url.pathname === `${base_url}/info`) {setTabberLoc('two')}
        else if (current_url.pathname === `${base_url}/gallery`) {setTabberLoc('three')}
        else if (current_url.pathname === `${base_url}/collections`) {setTabberLoc('four')}
        console.log(userData);
    }, [current_url.pathname]);

    return(
        <div className='screen__wrapper'>
            <div className='profile__content'>
                <div className='profile-header__wrapper'>
                    <div className='header-bg-img__wrapper'>
                        {userData.bg_url ?
                            <img src={userData.bg_url} alt="custom-background" /> :
                            <img src={default_bg} alt="default-background" />
                        }
                    </div>
                    <div className='user-distintion__wrapper'>
                        <div className='user-avatar'>
                            {userData.profilePic_url ?
                                <img src={userData.profilePic_url} alt="user_pic" /> :
                                <svg className='icon'>
                                    <use xlinkHref={svg_icons+'#icon-empty-user'}></use>
                                </svg>
                            }
                        </div>
                        <div className='user-names'>
                            <h2>{`${userData.name} ${userData.lastName}`}</h2>
                            <p>HeartPoints: {userData.heart_points}</p>
                        </div>
                        <div className='user-actions'>
                            <button className='button interaction-option'>
                                <svg className='icon'>
                                    <use xlinkHref={svg_icons+'#icon-heart-add'}></use>
                                </svg>
                                <p>AÃ±adir</p>
                            </button>
                            <button className='button interaction-option secundary'>
                                <svg className='icon'>
                                    <use xlinkHref={svg_icons+'#icon-chat'}></use>
                                </svg>
                                <p>Mensaje</p>
                            </button>
                        </div>
                    </div>
                </div>
                <hr />
                <div className='profile-navigation__wrapper'>
                    <div className={`profile-navigation__content ${tabberLoc}`}>
                        <Link to={`/app/profile/${username}/`} className={`button tabber ${tabberLoc === 'one' ? 'current' : ''}`}>
                            Historias
                        </Link>
                        <Link to={`/app/profile/${username}/info`} className={`button tabber ${tabberLoc === 'two' ? 'current' : ''}`}>
                            Informacion
                        </Link>
                        <Link to={`/app/profile/${username}/gallery`} className={`button tabber ${tabberLoc === 'three' ? 'current' : ''}`}>
                            Fotos
                        </Link>
                        <Link to={`/app/profile/${username}/collections`} className={`button tabber ${tabberLoc === 'four' ? 'current' : ''}`}>
                            Colecciones
                        </Link>
                    </div>
                </div>
                <Routes>
                    <Route path="/" element={<Publications_Section input_postList={postList_Test} />} />
                    <Route path="/info" element={<Information_Section />} />
                    <Route path="/gallery" element={<Gallery_Section />} />
                    <Route path="/collections" element={<Collections_Section />} />
                </Routes>
            </div>
        </div>
    );
}
