/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect, useState, useRef, useCallback } from 'react';
import { useParams } from 'react-router-dom'
import { AppContext } from '../../scripts/app_context';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';

import { request_userPosts, request_ProfileData } from '../../scripts/server_com';

/*----- Recursos multimedia -----*/
import default_bg from '../../imgs/loading_background.jpg';
import svg_icons from '../../icons/svg-resources.xml';

/*----- Funciones Generales -----*/
function formatedDate(dateString){
    const raw_date = new Date(dateString);
    const months = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

    const formated_date = `${raw_date.getDate().toString().padStart(2, '0')} de ${months[raw_date.getMonth()]} del ${raw_date.getFullYear()} • ${raw_date.getHours()}:${raw_date.getMinutes().toString().padStart(2, '0')}`;

    return formated_date
}

/*----- Componentes Auxiliares -----*/
const TagsHandler = ({input_tags, fullMode=false}) => {
    let local_elements = [];
    if (!fullMode) {
        input_tags.forEach((data, index) => {
            const localExisting_element = local_elements.find(element => element.key === index);
            if (!localExisting_element) {
                if (local_elements.length < 3 ) {
                    local_elements.push(
                        <div key={index} className='tagLabel'><p>#{data}</p></div>
                    )
                } else if (local_elements.length < 4 && input_tags.length >= 4) {
                    local_elements.push(
                        <div key={4} className='tagLabel'><p>{`+${input_tags.length - 3}`}</p></div>
                    )
                }
            }
        });
    } else {
        input_tags.forEach((data, index) => {
            const localExisting_element = local_elements.find(element => element.key === index);
            if (!localExisting_element) {
                local_elements.push(
                    <div key={index} className='tagLabel'><p>#{data}</p></div>
                )
            }
        });
    }

    return local_elements;
}

const PointsHandler = ({input_points}) => {
    return (
        <>
            <button className='button iconed'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
            <p>{input_points.total}</p>
            <button className='button iconed down'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
        </>
    )
}

/*----- Componentes locales -----*/
const Side_PublicationViewer = ({getter_post_id, setter_visibility, setter_currentPost}) => {
    const [ publicationInfo, setPublicationInfo ] = useState(null);

    useEffect(() => {
        if (getter_post_id) {
            setTimeout(() => { /* simulacion de busqueda de datos */
                setter_visibility(true)
                setPublicationInfo({
                    content: {
                        author_pic: null,
                        author: "tester 3000",
                        title: "Titulo de testeo Titulo de testeo Titulo de testeo Titulo de testeo Titulo de testeo Titulo de teste",
                        date: formatedDate("2024-06-19T06:30:00"),
