/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'
import { AppContext } from '../../scripts/app_context';
import { Routes, Route, Link, useLocation } from 'react-router-dom';

/*----- Recursos multimedia -----*/
import default_bg from '../../imgs/loading_background.jpg';
import svg_icons from '../../icons/svg-resources.xml';

/*----- Funciones Generales -----*/
function formatedDate(dateString){
    const raw_date = new Date(dateString);
    const months = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

    const formated_date = `${raw_date.getDate().toString().padStart(2, '0')} de ${months[raw_date.getMonth()]} del ${raw_date.getFullYear()} | ${raw_date.getHours()}:${raw_date.getMinutes().toString().padStart(2, '0')}`;

    return formated_date
}

/*----- Componentes Auxiliares -----*/
const TagsHandler = ({input_tags}) => {
    let local_elements = [];
    input_tags.forEach((data, index) => {
        const localExisting_element = local_elements.find(element => element.key === index);
        if (!localExisting_element) {
            if (local_elements.length < 3 ) {
                local_elements.push(
                    <div key={index} className='tagLabel'><p>#{data}</p></div>
                )
            } else if (local_elements.length < 4 && input_tags.length >= 4) {
                local_elements.push(
                    <div key={4} className='tagLabel'><p>{`+${input_tags.length - 3}`}</p></div>
                )
            }
        }
    });
    return local_elements;
}

const PointsHandler = ({input_points}) => {
    return (
        <>
            <button className='button iconed'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
            <p>{input_points.total}</p>
            <button className='button iconed down'>
                <svg className='icon'>
                    <use xlinkHref={svg_icons+'#icon-pointer'}></use>
                </svg>
            </button>
        </>
    )
}

/*----- Componentes locales -----*/
const PublicationViewer = ({current_post_id}) => {

}

const Publications_Section = ({ input_postList }) => {
    const [ galleryElements, setGalleryElements ] = useState([]);
    const [ listElements, setListElements ] = useState([]);
    const [ galleryMode, setGalleryMode ] = useState(true);
    const { commonHost } = useContext(AppContext);
    const [ localLoading, setLocalLoading ] = useState(true);
    const [ currentPost, setCurrentPost] = useState();

    useEffect(() => {
        if (!localLoading) {setLocalLoading(true)}
        if (input_postList) {
            input_postList.forEach((data, index) => {
                const galleryExistingElement = galleryElements.find(element => element.key === `card-gallery-${data.id}`);
                const listExistingElement = listElements.find(element => element.key === `card-list-${data.id}`);
                if (!galleryExistingElement) {
                    setGalleryElements(prevLista => prevLista.concat(<GalleryView key={`card-gallery-${data.id}`} input_post={data} input_host={commonHost}/>));
                }
                if (!listExistingElement) {
                    setListElements(prevLista => prevLista.concat(<ListView key={`card-list-${data.id}`} input_post={data} input_host={commonHost}/>))
                }
            });
            setLocalLoading(false)
        } else {
            setGalleryElements(prevLista => prevLista.concat(<h2>sin datos...</h2>));
            setListElements(prevLista => prevLista.concat(<h2>sin datos...</h2>));
            setLocalLoading(false)
        }
    }, [localLoading, setLocalLoading, galleryElements, input_postList, commonHost]);

    const ListView = ({ input_post, input_host }) => {
        return (
            <div data-id={input_post.id} className='card__wrapper'>
                <div className='card__header'>
                    <div className='post_tags'>
                        <TagsHandler input_tags={input_post.tags}/>
                    </div>
                    <h3 className='post_title'>{input_post.tittle}</h3>
                </div>
                <div className='card__postBody'>
                    <div className='post_content'>
                        <div>
                            <span className='post_author'>{input_post.author}:</span>
                            <div className='post_content-text'><p>{input_post.content}</p></div>
                        </div>
                    </div>
                </div>
                <div className='card__footer'>
                    <div className='post_points'>
                        <PointsHandler input_points={input_post.points}/>
                    </div>
                    <div className='post_comments-number'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-comment'}></use>
                        </svg>
                        <p>{input_post.n_comments}</p>
                    </div>
                    <div className='post_date'><p>{formatedDate(input_post.createdDate)}</p></div>
                </div>
                {input_post.media ?
                    <div className='card__media'>
                        <img src={input_post.media[0]} alt={`post-first-image`} />
                    </div> : ''
                }
            </div>
        )
    }

    const GalleryView = ({ input_post, input_host }) => {
        const PicturesHandler = ({input_images}) => {
            let local_elements = [];

            input_images.forEach((data, index) => {
                const localExisting_element = local_elements.find(element => element.key === index);
                if (!localExisting_element) {
                    console.log(`caso de indice ${index}; lista de entrada = ${input_images.length}`)
                    if (local_elements.length < 4) {
                        if (input_images.length === 1) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column full-row'>
                                    <img src={data} alt={`post-image-${1}`} />
                                </div>
                            )
                        } else if (input_images.length === 2) {
                            local_elements.push(
                                <div key={index} className='imageSection full-column'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            )
                        } else if (input_images.length === 3) {
                            if (index === 0) {
                                local_elements.push(
                                    <div key={index} className='imageSection full-column'>
                                        <img src={data} alt={`post-image-${index}`} />
                                    </div>
                                )  
                            } else {
                                local_elements.push(
                                    <div key={index} className='imageSection'>
                                        <img src={data} alt={`post-image-${index}`} />
                                    </div>
                                )    
                            }
                        } else {
                            local_elements.push(
                                <div key={index} className='imageSection'>
                                    <img src={data} alt={`post-image-${index}`} />
                                </div>
                            ) 
                        }
                    }
                }
            });
            return local_elements;
        }

        return (
            <div data-id={input_post.id} className='card__wrapper'>
                <div className='card__header'>
                    <div>
                        <span className='post_author'>{input_post.author}</span>
                        <p className='post_date'>{formatedDate(input_post.createdDate)}</p>
                    </div>
                    <h3 className='post_title'>{input_post.tittle}</h3>
                </div>
                <div className='card__postBody'>
                    {input_post.media ?
                        <div className='body-media'><PicturesHandler input_images={input_post.media}/></div>:
                        <div className='post_content-text'><p>{input_post.content}</p></div>
                    }
                    <div className='post_tags'>
                        <TagsHandler input_tags={input_post.tags}/>
                    </div>
                </div>
                <div className='card__footer'>
                    <div className='post_comments-number'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-comment'}></use>
                        </svg>
                        <p>{input_post.n_comments}</p>
                    </div>
                    <div className='post_points'>
                        <PointsHandler input_points={input_post.points}/>
                    </div>
                </div>
            </div>
        );
    }

    const PostHandler = () => {
        if (localLoading) {
            return <h2>Cargando...</h2>
        } else {
            if (galleryMode) {return galleryElements} else {return listElements}
        }
    }

    return(
        <div className='publications__wrapper'>
            <div className='publications__content'>
                <div className='header'>
                    <div className='post-searcher-creator'>
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-search'}></use>
                        </svg>
                        <input className='title_mod' type='text' placeholder='titulo a buscar o crear' />
                        <button className='button interaction-option'>
                            <svg className='icon'>
                                <use xlinkHref={svg_icons+'#icon-edit'}></use>
                            </svg>
                            <p>Nueva publicacion</p>
                        </button>
                    </div>
                    <div className='posts-filter'>
                        <div className='button-dropdown'>
                            <div className='button-dropdown__toggler'>
                                {galleryMode ?
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                        </svg>
                                        <span>Vista de galeria</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div> :
                                    <div>
                                        <svg className='icon'>
                                            <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                        </svg>
                                        <span>Vista de Lista</span>
                                        <svg className='icon arrow'>
                                            <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                                        </svg>
                                    </div>
                                }
                            </div>
                            <div className='button-dropdown__content'>
                                <button className='option' onClick={() => setGalleryMode(true)}>
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-gallery-view'}></use>
                                    </svg>
                                    <span>Vista de galeria</span>
                                </button>
                                <button className='option' onClick={() => setGalleryMode(false)}>
                                    <svg className='icon'>
                                        <use xlinkHref={svg_icons+'#icon-list-view'}></use>
                                    </svg>
                                    <span>Vista de Lista</span>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                <div className={`publications ${galleryMode ? 'gallery' : 'list'}`}>
                    <PostHandler />
                </div>
            </div>
            <div className='publication__aside hidden'></div>
        </div>
    );
}

