/*----- Importaciones y dependencias -----*/
import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from '../scripts/app_context';
import { request_login, request_register } from '../scripts/server_com';
import { Link } from 'react-router-dom';
import svg_icons from '../icons/svg-resources.xml'

/*----- Componente principal -----*/
export const AuthScreen = () => {
    const [ hasAccount, setHasAccount ] = useState(true);
    const [ keepLogged, setKeepLogged ] = useState(false);
    const { setAuth, setLogged } = useContext(AppContext);
    const [ screenMode, setScreenMode ] = useState('login');

    /* Verificacion de login local*/
    useEffect(() => {
        const local_auth = localStorage.getItem('auth');

        const handleAuthChange = () => {
            const newAuth = localStorage.getItem('auth');
            const newParsedAuth = JSON.parse(newAuth);
            setAuth(newParsedAuth);
            setLogged(true);
        };

        if (local_auth) {
            const parsedAuth = JSON.parse(local_auth);
            setAuth(parsedAuth); // Utilizar el token para autenticar al usuario automáticamente
            setLogged(true);
        }

        // Escuchar cambios en el almacenamiento local
        window.addEventListener('storage', handleAuthChange);

        return () => {
            // Limpiar el event listener al desmontar el componente
            window.removeEventListener('storage', handleAuthChange);
        };
    }, [setAuth, setLogged]);

    /* Manejo de interacciones en el componente de autenticacion */
    useEffect(() => {

    }, [screenMode]);

    function sendData() {
        if (hasAccount) {
            const username_input = document.getElementById('username');
            const password_input = document.getElementById('password');

            if (validateInput(username_input, "userspace") && validateInput(password_input, "password")) {
                request_login({ username: username_input.value, password: password_input.value }, (error, result) => {
                    if (error) {
                        if (error.data.error === "ERR_WRONG_PASSWORD") {
                            password_input.setCustomValidity('Contraseña invalida');
                            password_input.reportValidity();
                        } else if (error.data.error === "ERR_WRONG_USER") {
                            username_input.setCustomValidity('Usuario invalido');
                            username_input.reportValidity();
                        } else if (error.data.error === "INTERNAL_SERVER_ERROR") {
                            console.log("error en el servidor")
                        } else {
                            console.log(error);
                        }
                    } else {
                        if (result) {
                            const new_auth = {sessionToken: result.token, sessionUser: result.user}
                            if (keepLogged) {
                                localStorage.setItem('auth', JSON.stringify(new_auth));
                            }
                            setAuth(new_auth);
                            setLogged(true);
                        } else if (result == null) {
                            alert("Error al contactar con el servidor")
                        } else {
                            console.log('ERR_PAGE::SERVER_INVALID_RESPONSE', result);
                        }
                    }
                });
            }
        } else {
            const username_input = document.getElementById('new_username');
            const name_input = document.getElementById('new_name');
            const lastname_input = document.getElementById('new_lastname');
            const passworld_input = document.getElementById('new_password')

            if (validateInput(username_input, "userspace") && validateInput(name_input, "namespace") && 
            validateInput(lastname_input, "namespace") && validateInput(passworld_input, "password")) {
                request_register({ username: username_input.value, password: passworld_input.value, name: name_input.value, lastname: lastname_input.value, usertype: "US3R" }, (error, result) => {
                    if (error) {
                        console.log(error)
                    } else {
                        if (result) {
                            const new_auth = {sessionToken: result.token, sessionUser: result.user}
                            if (keepLogged) {
                                localStorage.setItem('auth', JSON.stringify(new_auth));
                            }
                            setAuth(new_auth);
                            setLogged(true);
                        } else if (result == null) {
                            alert("Error al contactar con el servidor")
                        } else {
                            console.log('ERR_PAGE::SERVER_INVALID_RESPONSE', result);
                        }
                    }
                })
            }
        }
    }

    function validateInput(element, typeString) {
        const validations = {
            namespace: /^[a-zA-ZñÑ\s]+$/,
            userspace: /^[a-zA-Z0-9_\-]+$/
        }
        if (!element.value) {
            element.setCustomValidity('Necesitas llenar este campo');
            element.reportValidity();
            return false;
        } else {
            if (typeString === "password") {
                if (element.value.length < 4) {
                    element.setCustomValidity('La contraseña debe ser de al menos 4 digitos');
                    element.reportValidity();
                    return false;  
                } else {
                    element.setCustomValidity('');
                    element.reportValidity();
                    return true;    
                }
            } else {
                if (element.value.match(validations[typeString])) {
                    element.setCustomValidity('');
                    element.reportValidity();
                    return true; 
                } else {
                    element.setCustomValidity('Formato invalido');
                    element.reportValidity();
                    return false;
                }
            }
        }
    }

    function hasAccount_toggle() {
        if (hasAccount) {setHasAccount(false);} else {setHasAccount(true);}
    }

    return (
        <div className={`auth__wrapper ${hasAccount ? 'visible-login' : 'visible-sign'}`}>
            <header className='header__wrapper'>
                <Link className='button link_like' to='/'>
                    <svg className='icon'>
                        <use xlinkHref={svg_icons+'#icon-arrow'}></use>
                    </svg>
                    <p>Regresar a la pagina de inicio</p>
                </Link>
                <div className='logotype'>
                    <svg className='icon'>
                        <use xlinkHref={svg_icons+'#icon-logotype'}></use>
                    </svg>
                </div>
            </header>
            <div className='auth__content_old'>
                <div className='login__wrapper'>
                    <div className='login__content'>
                        <h2>Iniciar sesion</h2>
                        <div className='entry form-full-entry__wrapper'>
                            <label htmlFor='username'>Nombre de usuario</label>
                            <input id='username' type='text' placeholder='Nombre de usuario...' autoComplete='true'/>
                        </div>
                        <div className='entry form-full-entry__wrapper'>
                            <label htmlFor='password'>Contraseña</label>
                            <input id='password' type='text' placeholder='Contraseña...' autoComplete='true'/>
                        </div>
                        <div className='entry form-full-entry__wrapper row-mode'>
                            <input id='keepSession' type='checkbox' onChange={() => keepLogged ? setKeepLogged(false) : setKeepLogged(true)}/>
                            <label htmlFor='keepSession'>Mantener sesion activa</label>
                        </div>
                        <button className='button submit' onClick={sendData}>Iniciar sesion</button>
                    </div>
                    <button className='button account' onClick={hasAccount_toggle}>¿No tienes cuenta? <span>¡Crea una!</span></button>
                </div>
                <div className='sign__wrapper'>
                    <div className='sign__content'>
                        <h2>Crear cuenta</h2>
                        <div className='entry form-full-entry__wrapper'>
                            <label htmlFor='new_username'>Nombre de usuario</label>
                            <input id='new_username' type='text' placeholder='Nombre de usuario...'/>
                        </div>
                        <div className='entry form-double-entry__wrapper'>
                            <label htmlFor='new_name'>Nombre</label>
                            <label htmlFor='new_lastname'>Apellido</label>
                            <input id='new_name' type='text' placeholder='Nombres...'/>
                            <input id='new_lastname' type='text' placeholder='Apellidos...'/>
                        </div>
                        <div className='entry form-full-entry__wrapper'>
                            <label htmlFor='new_password'>Contraseña</label>
                            <input id='new_password' type='text' placeholder='Contraseña...'/>
                        </div>
                        <div className='entry form-full-entry__wrapper row-mode'>
                            <input id='keepSession' type='checkbox' onChange={() => keepLogged ? setKeepLogged(false) : setKeepLogged(true)}/>
                            <label htmlFor='keepSession'>Mantener sesion activa</label>
                        </div>
                        <button className='button submit' onClick={sendData}>Crear cuenta</button>
                    </div>
                    <button className='button account' onClick={hasAccount_toggle}>¿Ya tienes cuenta? <span>¡Inicia sesion!</span></button>
                </div>
            </div>
            <div className='auth-form__wrapper'>
                <div className='avatar__wrapper'>
                    <svg className=''>
                        <use xlinkHref={svg_icons+'#icon-empty-user'}></use>
                    </svg>
                </div>
                <div className='row__wrapper tabber'>
                    <button onClick={() => setScreenMode('login')} className='current'>Iniciar sesion</button>
                    <button onClick={() => setScreenMode('sign')} className=''>Crear cuenta</button>
                </div>
                <div className='row__wrapper'>
                    <label htmlFor='i_email'>Email</label>
                    <input id='i_email' type="text" />
                </div>
                <div className={`row__wrapper double ${screenMode !== 'sign' ? 'hidden' : ''}`}>
                    <div className='row__wrapper'>
                        <label htmlFor='i_name'>Nombre</label>
                        <input id='i_name' type="text" />
                    </div>
                    <div className='row__wrapper'>
                        <label htmlFor='i_lastname'>Apellido</label>
                        <input id='i_lastname' type="text" />
                    </div>
                </div>
                <div className={`row__wrapper ${screenMode !== 'sign' ? 'hidden' : ''}`}>
                    <label htmlFor='i_birthdate'>Fecha de nacimiento</label>
                    <input id='i_birthdate' type="date" />
                </div>
                <div className={`row__wrapper ${screenMode !== 'sign' && screenMode !== 'login' ? 'hidden' : ''}`}>
                    <label htmlFor='i_password'>Contraseña</label>
                    <input id='i_password' type="text" />
                </div>
                <div className={`row__wrapper ${screenMode !== 'sign' ? 'hidden' : ''}`}>
                    <label htmlFor='i_password_again'>Repetir contraseña</label>
                    <input id='i_password_again' type="text" />
                </div>
                <div className='row__wrapper submit login'>
                    <button className='button submit'>Validar</button>
                    <button className={screenMode !== 'login' ? 'hidden' : ''} onClick={() => setScreenMode('forgot')} className='button link_like'>Olvidaste tu contraseña?</button>
                </div>
            </div>
        </div>
    )
};
