/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect, useState } from 'react';
import { AppContext } from '../../scripts/app_context';
import { Routes, Route, Link } from 'react-router-dom';

import { request_all_userData } from '../../scripts/server_com';

/*----- Recursos multimedia -----*/
import default_bg from '../../imgs/loading_background.jpg';
import svg_icons from '../../icons/svg-resources.xml';

/*----- Componentes locales -----*/
const UserCard = ({input_userData}) => {
    return (
        <div className='user-card__wrapper'>
            <div className='user-deco__wrapper'>
                <div className='user-background'>
                    {input_userData.bg_url ?
                        <img src={input_userData.bg_url} alt="custom-background" /> :
                        <img src={default_bg} alt="default-background" />
                    }
                </div>
                <div className='user-picture'>
                    {input_userData.profilePic_url ?
                        <img src={input_userData.profilePic_url} alt="custom-avatar" /> :
                        <svg className='icon'>
                            <use xlinkHref={svg_icons+'#icon-empty-user'}></use>
                        </svg>
                    }
                </div>
            </div>
            <div className='user-info__wrapper'>
                <h2>{`${input_userData.name} ${input_userData.lastName}`}</h2>
                <p>{input_userData.birthdate}</p>
            </div>
        </div>
    );
}

/*----- Componente central -----*/
export const ExplorePanel = () => {
    const { auth, userData } = useContext(AppContext);
    const [ allUsers, setAllUsers ] = useState([]);

    useEffect(() => {
        if (auth) {
            if (!allUsers) {
                request_all_userData(auth, (error, result) => {
                    if (error) {
                        console.log(error)
                    } else {
                        setAllUsers(result)
                    }
                });
            } else {
                console.log(allUsers)
            }
        } else {
            console.warn("aun no carga el auth");
        }
    }, [auth, allUsers, setAllUsers]);

    const RenderCards = () => {
        const elements = [];
        allUsers.forEach((data, index) => {
            elements.push(<UserCard key={`card-${index}`} input_userData={data}/>)
        });
        return elements;
    }

    return(
        <div className='screen__wrapper'>
            <div className='explore__content'>
                <RenderCards />
            </div>
        </div>
    );
}
