/*----- Importaciones y dependencias -----*/
import React, { useContext, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, useNavigate, Routes, Route } from 'react-router-dom';

import { AppContextProvider } from './scripts/app_context';
import { AppContext } from './scripts/app_context';

import './stylesheets/app_styles.css';
import './stylesheets/main_styles.css';

/*----- Componentes -----*/
import { LandingScreen } from './components/landing_screen';
import { AuthScreen } from './components/auth_screen';

/*----- Componente principal -----*/
const MainBody = () => {
    /*
    const { auth } = useContext(AppContext);
    const navigate = useNavigate();
    */

    /*
    useEffect(() => {
        if (!auth && window.location.pathname !== '/') {
            navigate('/');
        } else if (auth && window.location.pathname === '/') {
            navigate('/app/home')
        }
    }, [auth, navigate]);
    */

    return (
        <Routes>
            <Route path="/" element={<><LandingScreen /></>} />
            <Route path="/login" element={<><AuthScreen /></>} />
            <Route path="/app/*" element={<><AppScreen /></>} />
        </Routes>
    )
}

ReactDOM.createRoot(document.getElementById('app__mount')).render(
    <AppContextProvider>
        <BrowserRouter>
            <MainBody />
        </BrowserRouter>
    </AppContextProvider>
);
