/*----- Importaciones y dependencias -----*/
import { createContext } from 'react';
import React, { useState } from 'react';

export const AppContext = createContext();

/*----- Context Provider -----*/
export const AppContextProvider = ({ children }) => {
    const [auth, setAuth] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [logged, setLogged] = useState(false);
    const [userData, setUserData] = useState({});
    const [commonHost, setCommonHost] = useState("https://blue22feather.pythonanywhere.com"); /*window.location.host  <-- para cuando el servidor este montado --> http://localhost:5000*/

    const [localCounter, setLocalCounter] = useState(0)

    return (
        <AppContext.Provider value={{ localCounter, userData, auth, isLoading, logged, setAuth, setIsLoading, setLogged, setUserData, commonHost, setCommonHost, setLocalCounter }}>
            {children}
        </AppContext.Provider>
    );
};
